    // var currentLang = $translate.proposedLanguage() || $translate.use();
    self.titleKey = self.isAddMode ? 'CLIENT_EDIT_HEADER_TEXT_ADD' : 'CLIENT_EDIT_HEADER_TEXT_CHANGE';
